ST.xhtml:
ptr := ADR(GVL.Group_01_Axis_01);
MEMSET(destAddr:=ADR(res[0]), fillByte:=0, n:=SIZEOF(res));
FOR ig := 1 TO 8 DO
	FOR im := 0 TO 15 DO
		IF ptr^ THEN
			IF res[im] <> 0 THEN
				ptr^ := 0;
			ELSE
				res[im] := ig;
			END_IF
		END_IF
		ptr := ptr + 1;
	END_FOR
END_FOR

GetMotorsGroup := 0;
RETURN;

